import React from 'react'
import NumberInput from '../../components/ui/NumberInput'
import SelectInput from '../../components/ui/SelectInput'
import TextInput from '../../components/ui/TextInput'
import useFormContext from '../../Hooks/useFormContext'

const EducationInfo = () => {
  const { userData, handleChange, stepTitle, step } = useFormContext()

  const yearOptions = [
    ["Freshman - First Year", "freshman"],
    ["Sophomore - Second Year", "sophomore"],
    ["Junior - Third Year", "junior"],
    ["Senior - Fourth Year", "senior"]
  ]

  return (
    <div>
      <h1>{stepTitle[step]}</h1>
      <TextInput
        labelText="University/College"
        fieldName="universityName"
        fieldValue={userData.universityName}
        handleChange={handleChange}
      />
      <TextInput
        labelText="Major"
        fieldName="major"
        placeholder="Computer Science"
        fieldValue={userData.major}
        handleChange={handleChange}
      />
      <NumberInput
        labelText="Graduation Year"
        fieldName="graduationYear"
        min="2023"
        fieldValue={userData.graduationYear}
        handleChange={handleChange}
      />
      <SelectInput
        labelText="Year of Undergraduate Education"
        fieldName="yearOfUndergrad"
        fieldValue={userData.yearOfUndergrad}
        handleChange={handleChange}
        options={yearOptions}
      />
    </div>
  )
}

export default EducationInfo
