import React from 'react'
import EmailInput from '../../components/ui/EmailInput'
import TextInput from '../../components/ui/TextInput'
import NumberInput from '../../components/ui/NumberInput'
import useFormContext from '../../Hooks/useFormContext'

const BasicInfo = () => {
  const { userData, handleChange, stepTitle, step } = useFormContext()

  // console.log(userData.isMSUStudent)
  return (
    <div>
      <h1>{stepTitle[step]}</h1>
      <TextInput
        labelText="First Name"
        fieldName="firstName"
        placeholder="Alan"
        fieldValue={userData.firstName}
        handleChange={handleChange}
      />
      <TextInput
        labelText="Last Name"
        fieldName="lastName"
        placeholder="Turing"
        fieldValue={userData.lastName}
        handleChange={handleChange}
      />
      <NumberInput
        labelText="Phone Number"
        fieldName="phone"
        placeholder="9805171234"
        minLength="10"
        fieldValue={userData.phone}
        handleChange={handleChange}
      />
      <EmailInput
        labelText="Student email"
        fieldName="email"
        placeholder="alan.turing@princeton.edu"
        pattern=".+@msu\.edu"
        fieldValue={userData.email}
        handleChange={handleChange}
      />
      <TextInput
        labelText="MSU Net ID"
        fieldName="msuNetID"
        placeholder="alan.turing"
        fieldValue={userData.msuNetID}
        handleChange={handleChange}
        disabled={!userData.isMSUStudent}
      />
    </div>
  )
}

export default BasicInfo
