import React from 'react'
import EmailInput from '../../components/ui/EmailInput'
import TextInput from '../../components/ui/TextInput'
import useFormContext from '../../Hooks/useFormContext'

const BasicInfo = () => {
  const { userData, handleChange, isStudent, commonInputSetContainerClasses, commonStepFormContainerClasses } = useFormContext()
  return (
    <div className={commonStepFormContainerClasses}>
      <div className={commonInputSetContainerClasses}>
        <TextInput containerClass={(userData.firstName) ? " border-green-300" : ""}
          labelText="First Name"
          fieldName="firstName"
          placeholder="Alan"
          fieldValue={userData.firstName}
          handleChange={handleChange}
          required
        />
        <TextInput containerClass={(userData.lastName) ? " border-green-300" : ""}
          labelText="Last Name"
          fieldName="lastName"
          placeholder="Turing"
          fieldValue={userData.lastName}
          handleChange={handleChange}
          required
        />
      </div>
      <div className={commonInputSetContainerClasses}>
        <TextInput containerClass={(userData.phone.length === 14) ? " border-green-300" : ""}
          labelText="Phone Number"
          fieldName="phone"
          placeholder="9805171234"
          fieldValue={userData.phone}
          handleChange={handleChange}
          required
        />
        <EmailInput containerClass={((userData.email) ? ((isStudent) ? " border-green-300" : " border-red-300") : "")}
          labelText="Student email"
          fieldName="email"
          placeholder="turing.alan@princeton.edu"
          pattern="/^[a-z0-9._%+-]+@[a-z0-9.-]+\.edu$/"
          minLength="6"
          fieldValue={userData.email}
          handleChange={handleChange}
          required
        />
      </div>
    </div>
  )
}

export default BasicInfo
