import { createContext, useState, useEffect } from "react";

const FormContext = createContext({})

export const FormProvider = ({ children }) => {
  const stepTitle = {
    1: "Basic Info",
    2: "Education",
    3: "Personal Info",
    4: "Experience",
    5: "Forms & Agreements"
  }

  const [step, setStep] = useState(1)

  const [userData, setUserData] = useState({
    step: 1,
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    isMSUStudent: false,
    msuNetID: "",
    universityName: "",
    major: "",
    yearOfUndergrad: "",
    educationLevel: "",
    countryOfOrigin: "",
    dateOfBirth: "",
    minor: false,
    gender: "",
    race: "",
    hackathonsAttended: "",
    linkedinURL: "",
    resume: "",
    minorForm: "",
    mlhAgreements: ""
  })

  useEffect(() => {
    if (userData.email.slice(-7) === "msu.edu") {
      setUserData(prevUserData => ({
        ...prevUserData,
        isMSUStudent: true,
        msuNetID: userData.email.slice(0, userData.email.indexOf("@"))
      }))
    } else {
      setUserData(prevUserData => ({
        ...prevUserData,
        isMSUStudent: false,
        msuNetID: ""
      }))
    }
  }, [userData.email, userData.isMSUStudent])

  const handleChange = e => {
    const type = e.target.type
    const name = e.target.name

    const value = type === "checkbox"
      ? e.target.checked
      : e.target.value

    setUserData(prevUserData => ({
      ...prevUserData, [name]: value
    }))
  }

  const {
    isMSUStudent,
    msuNetID,
    linkedinURL,
    minorForm,
    ...requiredFields
  } = userData

  const canSubmit = [...Object.values(requiredFields)].every(Boolean) && step === Object.keys(stepTitle).length

  const x = "test"

  return (
    <FormContext.Provider value={{
      stepTitle, step, setStep, userData, setUserData, canSubmit, handleChange,
      x
    }}>
      {children}
    </FormContext.Provider>
  )
}

export default FormContext;
