import { createContext, useState, useEffect } from "react";

const FormContext = createContext({})

export const FormProvider = ({ children }) => {
  const stepTitle = {
    1: "Basic Info",
    2: "Education",
    3: "Personal Info",
    4: "Experience",
    5: "Forms & Agreements"
  }

  const [step, setStep] = useState(1)

  const [isStudent, setIsStudent] = useState(false)

  const [userData, setUserData] = useState({
    step: 1,
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    universityName: "",
    major: "",
    yearOfUndergrad: "",
    graduationYear: "",
    countryOfOrigin: "",
    dateOfBirth: "",
    minor: false,
    sex: "",
    race: "",
    hackathonsAttended: "",
    linkedinURL: "",
    githubURL: "",
    resume: "",
    minorForm: "",
    mlhCoCAgree: false,
    mlhTCAgree: false,
    mlhEmailsAgree: false,
  })

  const basicInfoData = ["firstName", "lastName", "email", "phone"]
  const educationInfoData = ["universityName", "major", "graduationYear", "yearOfUndergrad"]
  const personalInfoData = ["countryOfOrigin", "dateOfBirth", "sex", "race"]
  const experienceInfoData = [["hackathonsAttended", "resume"], ["linkedin", "github"]]
  const agreementsInfoData = [["mlhCoCAgree", "mlhTCAgree"], ["mlhEmailsAgree"]]

  useEffect(() => {
    if (userData.email.match(/^[a-z0-9._%+-]+@[a-z0-9.-]+\.edu$/)) {
      setIsStudent(true);
    } else {
      if (userData.email)
        setIsStudent(false);
    }
  }, [userData.email, isStudent])

  // console.log(userData.mlhCoCAgree)

  const handleChange = e => {
    const type = e.target.type
    const name = e.target.name

    // const value = type === "checkbox"
    //   ? e.target.checked
    //   : e.target.value

    setUserData(prevUserData => ({
      ...prevUserData, [name]: value
    }))
  }

  const {
    linkedinURL,
    githubURL,
    minorForm,
    mlhEmailsAgree,
    ...requiredFields
  } = userData



  const canProceedFromBasic = basicInfoData.map(key => userData[key]).every(Boolean) && (isStudent)
  const canProceedFromEducation = educationInfoData.map(key => userData[key]).every(Boolean) && !(userData["graduationYear"] <= 2022)
  const canProceedFromPersonal = personalInfoData.map(key => userData[key]).every(Boolean)
  const canProceedFromExperience = experienceInfoData[0].map(key => userData[key]).every(Boolean)
  const canProceedFromAgreements = agreementsInfoData[0].map(key => userData[key]).every(Boolean)

  const canSubmit = ([...Object.values(requiredFields)].every(Boolean)) && (step === Object.keys(stepTitle).length) && !canProceedFromAgreements

  const disabledPrev = step === 1
  const disabledNext = (step === Object.keys(stepTitle).length)
    || (step === 1 && !canProceedFromBasic)
    || (step === 2 && !canProceedFromEducation)
    || (step === 3 && !canProceedFromPersonal)
    || (step === 4 && !canProceedFromExperience)

  const hideNext = step === Object.keys(stepTitle).length
  const hideSubmit = step !== Object.keys(stepTitle).length

  console.log(canProceedFromAgreements)

  const commonStepFormContainerClasses = "flex flex-col w-full h-[500px] "
  const commonInputSetContainerClasses = "flex flex-row justify-between w-full gap-x-4 "
  const commonContainerClasses = "mb-4 p-4 border w-full rounded-md "
  const commonLabelClasses = "uppercase text-xs text-gray-400 "
  const commonInputClasses = "block w-full py-4 px-0 outline-none "
  const commonAdInfoClasses = " "

  return (
    <FormContext.Provider value={{
      stepTitle, step, setStep, userData, setUserData, canSubmit, handleChange,
      canProceedFromBasic, canProceedFromEducation, canProceedFromPersonal, canProceedFromExperience,
      disabledNext, disabledPrev, hideNext, hideSubmit, isStudent,
      commonContainerClasses, commonLabelClasses, commonInputClasses, commonAdInfoClasses,
      commonInputSetContainerClasses, commonStepFormContainerClasses
    }}>
      {children}
    </FormContext.Provider>
  )
}

export default FormContext;
