import React from 'react'
import useFormContext from '../../Hooks/useFormContext'

function TextInput(props) {
  const { commonContainerClasses, commonLabelClasses, commonInputClasses, commonAdInfoClasses } = useFormContext()

  return (
    <div hidden={props.hidden} className={commonContainerClasses + props.containerClass + " flex flex-col justify-start"}>
      <div>
        <label
          htmlFor={props.fieldName}
          className={commonLabelClasses + props.labelClass + " pl-4 inter-font"}
        >{props.labelText}</label>
      </div>
      <div >
        <input type="checkbox"
          className={commonInputClasses + props.inputClass + " h-full"}
          id={props.fieldName}
          name={props.fieldName}
          checked={props.fieldValue}
          onChange={props.handleChange}
          disabled={props.disabled}
        />
        <span>{props.addInfo}</span>
      </div>
    </div>
  )
}

export default TextInput
